var pageHeight=$("#home").height(),pageWidth=$("#home").width(),scrollAnimation=function(a,b){return $("#slides").transition({x:pageWidth*-a,y:pageHeight*-b},1500,"in"),!1};$("#navHome").click(function(){scrollAnimation(0,0)}),$("#navProfile").click(function(){scrollAnimation(0,1)}),$("#navAbout").click(function(){scrollAnimation(1,1)}),$("#navContact").click(function(){scrollAnimation(1,0)});var date=new Date,currentYear=date.getFullYear();$(".year").text(currentYear);